--
-- Структура таблицы `indicators`
--

CREATE TABLE IF NOT EXISTS `indicators` (
  `id` int(11) NOT NULL,
  `name` varchar(32) NOT NULL,
  `code` varchar(8) NOT NULL,
  `level` integer NOT NULL,
  `ru` varchar(128) NOT NULL,
  `eng` varchar(256) NOT NULL,
  `description` text NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=cp1251 COMMENT='Финансовые показатели';

--
-- Дамп данных таблицы `indicators`
--


--
-- Структура таблицы `indicators`
--

CREATE TABLE IF NOT EXISTS `values` (
  `id` int(11) NOT NULL,
  `indicator` int(11) NOT NULL,
  `period` varchar(8) NOT NULL,
  `value` varchar(8) NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=cp1251 COMMENT='Значения финансовых показателей';
