INSERT INTO `indicators` (`id`, `name`, `code`, `level`, `ru`, `eng`, `description`) VALUES
(1101, 'revenue',                       'R',  0, 'Выручка', 'Revenue',                                          'Выручка (англ. revenue) - количество денежных средств или иных благ, получаемое компанией за определенный период ее деятельности, в основном за счет продажи продуктов или услуг своим клиентам. Выручка от реализации продукции (работ, услуг) включает в себя денежные средства либо иное имущество в денежном выражении, полученные или подлежащие получению в результате реализации товаров, готовой продукции, работ, услуг по ценам, тарифам в соответствии с договорами. '),
(1102, 'amortization and depreciation', 'DA', 3, 'Амортиз.', 'Amortization and depreciation',                   'Амортизация (англ. amortization and depreciation) - процесс перенесения по частям стоимости основных средств и нематериальных активов по мере их физического или морального износа на стоимость производимого продукта.\r\n'),
(1103, 'other tax',                     '',   3, 'Налоги кр. налога на прибыль', 'Other tax',                   'Налоги, кроме налога на прибыль - налоги, акцизы, входящие в операционные издержки. '),
(1104, 'operating expenses',            '',   1, 'Операц. издержки', 'Operating expenses',                      'Операционные расходы (англ. Operating expenses) - затраты и платежи, связанные с проведением за определенный период времени финансовых, производственных, хозяйственных операций. Операционные расходы включают затраты на производство и реализацию продукции, административные и финансовые расходы.'),
(1105, 'operation profit',              '',   0, 'Операц. прибыль (EBIT)', 'Earnings Before Interest and Taxes','Операционная прибыль (EBIT) (сокр. от англ. Earnings Before Interest and Taxes) - разность между валовой прибылью и операционными затратами. Прибыль хозяйствующего субъекта в результате основной экономической деятельности. Представляет собой остаток при вычитании из торговой прибыли операционных накладных расходов (рента, амортизационные отчисления за здания и оборудование, расходы на ГСМ и иные текущие расходы). Операционная прибыль - финансовый результат от всех видов деятельности до уплаты налога на прибыль и процентов по заемным средствам.\n'),
(1106, 'finance income',                '',   1, 'Проц. получен.', 'Interest income',                           'Проценты полученные (англ. Interest income) - средства, полученные компанией в отчетном периоде в качестве компенсации за использование ее финансовых активов. (Проценты по вкладам и другие инвестиции)\n'),
(1107, 'finance costs',                 '',   1, 'Проц. уплачен.', 'Interest expense',                          'Проценты уплаченные (англ. Interest expense) - часть стоимости использования компанией заемных средств, выплаченная за отчетный период. Данная статья расходов уменьшает налогооблагаемую базу компании.\n'),
(1108, 'interest on loans',             '',   1, 'Проц. по кредит.', '',                                        'Проценты по кредитам - разница процентов полученных и процентов уплаченных.\n'),
(1109, 'other income',                  '',   0, 'Внеоперац. дох/изд', 'Other income',                          'Внеоперационные доходы/издержки - доходы и издержки компании, возникающие в результате деятельности, не связанной с ее основной специализацией. (Предоставление сторонних работ и услуг, прибыль/убытки от выбытия активов, штрафы и пени и проч.) '),
(1110, 'ebitda',                        '',   1, 'EBITDA', 'Earnings before Interest, Taxes, Depreciation and Amortization', 'EBITDA (сокр. от англ. Earnings before Interest, Taxes, Depreciation and Amortization) — аналитический показатель, равный объему прибыли до вычета расходов по процентам, уплаты налогов и амортизационных отчислений. '),
(1111, 'profit before tax',             '',   0, '(EBT) Приб. до налогов', 'Earings before Taxes',              '(EBT) Прибыль до налогов - (сокр. от англ. Earings before Taxes) - прибыль компании до вычета налога на прибыль. '),
(1112, 'profit tax',                    '',   1, 'Налог на прибыль', 'profit tax',                              'Налог на прибыль (англ. profit tax) - прямой налог, взимаемый с прибыли организации (предприятия, банка, страховой компании и т. д.) Базовая ставка составляет 20%. '),
(1113, 'non-controlling interest',      '',   1, 'Доля меньш. (отч)', 'Non-controlling interest',               'Доля меньшинства (англ. Minority interest), или неконтролирующая доля (англ. Non-controlling interest) - в консолидированном отчете о прибылях и убытках отражает ту часть чистого дохода группы, которая причитается сторонним организациям. '),
(1114, 'net income',                    '',   0, 'Чистая прибыль', 'Net profit',                                'Чистая прибыль (англ. net profit) - часть балансовой прибыли предприятия, остающаяся в его распоряжении после уплаты налогов, сборов, отчислений и других обязательных платежей в бюджет. Чистая прибыль используется для увеличения оборотных средств предприятия, формирования фондов и резервов и реинвестиций в производство. '),
(1115, 'convergence report',            '',   0, 'Сходимость отчета', 'Convergence report',                     'Сходимость отчета о прибылях и убытках - расчитывается по формуле: Выручка-Операционные издержки+Внеоперационные доходы/издержки+Доля меньшинства (отчет о прибылях и убытках)+Налог на прибыль-Чистая прибыль '),
(1001, 'period and type',               '',   0, 'Период/тип отч.', '', ''),
(1201, 'inventories',                   '',   2, 'Запасы', 'Inventories',                                       'Запасы (англ. inventories) — относятся к краткосрочным активам предприятия, от которых ожидается получение экономической выгоды. Запасы могут храниться преприятием с целью продажи в ходе нормальной деятельности; находиться в в процессе производства или храниться в форме сырья или материалов, предназначенных для использования в производственном процессе.'),
(1202, 'property plant and equipment',  '',   1, 'Основные средства', 'Property, plant and equipment',          'Основные средства (англ. property, plant and equipment) - собственность, здания и оборудование - долгосрочные, или нетекущие, не предназначенные для продажи в данном отчетном периоде активы, используемые в деятельности компании. '),
(1203, 'non-current assets',            '',   2, 'Долгоср. активы', 'Non-current assets',                       'Долгосрочные или внеоборотные активы (англ. non-current assets) - активы с продолжительностью использования более одного года: долгосрочные финансовые вложения, нематериальные активы, основные средства, прочие долгосрочные активы.\n'),
(1204, 'receivables',                   '',   2, 'Дебиторская задолженность', 'Receivables',                    'Дебиторская задолженность (англ. receivables) - сумма долгов, причитающихся предприятию, от юридических или физических лиц в итоге хозяйственных взаимоотношений с ними. Обычно долги образуются от продаж в кредит. '),
(1205, 'short-term investments',        '',   2, 'Краткосрочные финансовые вложения','Short-term investments',  'Краткосрочные финансовые вложения (англ. short-term investments) - инвестиции со сроком до 1 года, вложение излишка денежных средств в ценные бумаги. Краткосрочные инвестиции выгодны в период действия высоких процентных ставок. '),
(1206, 'cash',                          '',   2, 'Ден. ср-ва и эквиваленты', 'Cash and cash equivalents',       'Денежные средства и их эквиваленты (англ. Cash and cash equivalents) - деньги в кассе и на текущих счетах, краткосрочные и высоколиквидные вложения, легко обратимые в заранее известную денежную сумму и мало подверженные риску потери стоимости. Временной критерий эквивалента денежных средства - трехмесячный срок погашения (или иной, аналогично короткий срок). '),
(1207, 'current assets',                '',   2, 'Краткоср. активы', 'Current assets',                          'Краткосрочные или текущие (оборотные) активы (англ. current assets) — активы, которые в течение одного производственного цикла или одного года могут быть обращены в денежные средства. Текущие активы включают в себя денежные средства компании на счету в банке, краткосрочную дебиторскую задолженность, ликвидные ценные бумаги и другие активы.\n'),
(1208, 'assets',                        '',   0, 'Актив', 'Assets',                                             'Актив (англ. assets) - часть бухгалтерского баланса, отражающая состав и стоимость имущества организации на определенную дату. Актив - вероятные будущие экономические выгоды, полученные или контролируемые конкретной организацией в результате прошлых сделок или событий.\n'),
(1209, 'equity',                        '',   2, 'Акционерн. капитал', 'Equity',                                'Акционерный капитал (англ. equity) - представляет собой совокупность финансовых ресурсов компании, сформированных за счет средств учредителей (участников) и финансовых результатов собственной деятельности. Как показатель балансового отчета, представляет собой: - уставный капитал (оплаченный акционерный капитал); - нераспределенную прибыль, заработанную предприятием в результате эффективной деятельности и остающуюся в его распоряжении; - добавочный капитал (формируется по результатам переоценки активов, за счет эмиссионного дохода; безвозмездно полученные предприятием ценности); - резервный капитал — резервный фонд; фонд потребления и т. п. '),
(1210, 'non-controlling interest',      '',   2, 'Доля меньш. (бал)', 'Non-controlling interest',               'Доля меньшинства (англ. Minority interest), или неконтролирующая доля (англ. Non-controlling interest) - часть прибыли или убытка и чистых активов дочернего предприятия, приходящаяся на доли участия в капитале, которыми материнское предприятие не владеет прямо или косвенно через дочерние предприятия. '),
(1211, 'non-current liabilites',        '',   2, 'Долгоср. долг', 'Non-current liabilites',                     'Долгосрочный долг (англ. Non-current liabilites) - это задолженность организации по кредитам и займам, срок исполнения которых превышает 12 месяцев. К долгосрочным обязательствам также относятся отложенные налоговые обязательства. '),
(1212, 'accounts payable',              '',   3, 'Кредиторская задолженность', 'Accounts payable',              'Кредиторская задолженность (англ. accounts payable) - задолженность субъекта (предприятия, организации, физического лица) перед другими лицами, которую этот субъект обязан погасить. Кредиторская задолженность возникает в случае, если дата поступления услуг (работ, товаров, материалов и т. д.) не совпадает с датой их фактической оплаты.\n'),
(1213, 'current liabilities',           '',   2, 'Краткср. долг', 'Current liabilities',                        'Краткосрочный долг или текущие обязательства (англ. current liabilities) - денежные суммы, которые организация должна оплатить кредиторам в течение непродолжительного периода времени, обычно в течение одного года. '),
(1214, 'liabilities',                   '',   1, 'Обязательства', 'Liabilities',                                'Обязательства (англ. liabilities) - вероятные будущие потери экономических выгод, вытекающие из существующих в настоящий момент обязательств по передаче активов или предоставлению услуг другим организациям в будущем в результате прошлых сделок или событий. '),
(1215, 'liabilities and equity',        '',   0, 'Пассив', 'Liabilities and equity',                            'Пассив (англ. liabilities and equity) - сумма обязательств и собственного капитала.Равен активу. '),
(1216, 'balance convergence',           '',   0, 'Сходимость баланса', 'Balance convergence',                   'Сходимость баланса - расчитывается по формуле: Долгосрочный долг+Краткосрочный долг+Акционерный капитал+Доля меньшинства (Баланс) - Активы компании '),
(1301, 'сash flow',                     '',   0, 'Операц. ден. поток', 'Cash flow from operating activities',   'Денежный поток от операционной деятельности (англ. сash flow from operating activities - сумма чистой прибыли и амортизации минус увеличение собственных оборотных (кроме денежных) средств за отчетный период. Денежный поток от операционной деятельности - денежный поток, связанный с основной деятельностью компании.'),
(1302, 'investing activities',          '',   0, 'Инвестиц. ден. поток', 'Cash flow from investing activities', 'Денежный поток от инвестиционной деятельности (англ. cash flow from investing activities) - чистое изменение постоянных активов. '),
(1303, 'financing activities',          '',   0, 'Финанс. ден. поток', 'Cash flow from financing activities',   'Денежный поток от финансовой деятельности (англ. Cash flow from financing activities) - денежный поток, который образуется за счет привлечения новых источников капитала плюс увеличение процентных обязательств, минус выплаченные дивиденды. '),
(1304, '',                              '',   0, 'Влияние изм. курса', '',                                      'Влияние изменения курса - в отчетности возникают курсовые разницы в связи с пересчетом статей в иностранной валюте в функциональную валюту. '),
(1305, 'Total cash flow',               '',   0, 'Изм. ден. потока', 'Total cash flow',                         'Совокупный денежный поток (англ. Total cash flow) - сумма денежных потоков от операционной, инвестиционной и финансовой деятельности предприятия. ');
